import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import {RouterModule, Routes} from '@angular/router';
import { ReactiveFormsModule } from '@angular/forms';
import { AppComponent } from './app.component';
import { TopBarComponent } from './top-bar/top-bar.component';
import { ProductListComponent } from './products/phones/phone-list/product-list.component';
import { ProductAlertsComponent } from './products/phones/phone-alerts/product-alerts.component';
import { WatchesComponent } from './products/watches/watches.component';
import { LaptopsComponent } from './products/laptops/laptops.component';
import { NotFoundComponent } from './products/not-found/not-found.component';

@NgModule({
  imports: [
    BrowserModule,
    ReactiveFormsModule,
    RouterModule.forRoot([
      {path: '', component: ProductListComponent},
      {path: 'watches', component: WatchesComponent},
      {path: 'laptop', component: LaptopsComponent},
      {path: '**', component: NotFoundComponent}
    ])
  ],
  declarations: [
    AppComponent,
    TopBarComponent,
    ProductListComponent,
    ProductAlertsComponent,
    WatchesComponent,
    LaptopsComponent,
    NotFoundComponent,
  ],
  bootstrap: [
    AppComponent
  ]
})


export class AppModule { }


